import React from 'react';
import circle1 from '../../../assets/middle/circle_mid_green.png';
import circle2 from '../../../assets/middle/circle_mid_violet.png';
import circle3 from '../../../assets/middle/circle_mid_white.png';
import heart1 from '../../../assets/middle/heart_mid_green.png';
import heart2 from '../../../assets/middle/heart_mid_violet.png';
import heart3 from '../../../assets/middle/heart_mid_white.png';
import square1 from '../../../assets/middle/square_mid_green.png';
import square2 from '../../../assets/middle/square_mid_violet.png';
import square3 from '../../../assets/middle/square_mid_white.png';

const middleMap = {
  circle: [circle1, circle2, circle3],
  heart: [heart1, heart2, heart3],
  square: [square1, square2, square3],
};

function CreamMiddleOptions({ shapeName, onSelect }) {
  if (!shapeName) return null;
  const creams = middleMap[shapeName];

  return (
    <div>
      <label className="block font-semibold mb-2">
        Kem ở giữa (cho bánh 2 tầng):
      </label>
      <div className="flex space-x-4">
        {creams.map((src, index) => (
          <img
            key={index}
            src={src}
            alt={`middle-${index}`}
            draggable
            onClick={() => onSelect(src)}
            onDragStart={(e) => {
              e.dataTransfer.setData('type', 'creamMiddle');
              e.dataTransfer.setData('data', src);
            }}
            className="w-16 h-16 cursor-move border hover:border-blue-500 rounded shadow"
          />
        ))}
      </div>
    </div>
  );
}

export default CreamMiddleOptions;
