import React from 'react';
import ShapeOptions from './steps/ShapeOptions';
import ColorOptions from './steps/ColorOptions';
import CreamMiddleOptions from '/steps/CreamMiddleOptions';
import ColorTier2Options from '/steps/ColorTier2Options';
import CreamTopOptions from '/steps/CreamTopOptions';
import SmoothLinesOptions from '/steps/SmoothLinesOptions';

function Sidebar({
  onSelectShape,
  onSelectColorTier1,
  onSelectCreamMiddle,
  onSelectColorTier2,
  onSelectCreamTop,
  onSelectSmoothLine,
  selectedShape
}) {
  return (
    <div className="w-1/2 p-4 space-y-6 overflow-auto max-h-screen">
      <ShapeOptions onSelect={onSelectShape} />
      <ColorOptions shapeName={selectedShape?.name} onSelect={onSelectColorTier1} />
      <CreamMiddleOptions shapeName={selectedShape?.name} onSelect={onSelectCreamMiddle} />
      <ColorTier2Options shapeName={selectedShape?.name} onSelect={onSelectColorTier2} />
      <CreamTopOptions shapeName={selectedShape?.name} onSelect={onSelectCreamTop} />
      <SmoothLinesOptions
        shapeName={selectedShape?.name}
        onSelect={onSelectSmoothLine}
        onSkip={() => onSelectSmoothLine(null)}
      />
    </div>
  );
}

export default Sidebar;
