import React from 'react';
import circle from '../../../assets/shape/circle_form.png';
import heart from '../../../assets/shape/heart_form.png';
import square from '../../../assets/shape/square_form.png';

const shapes = [
  { name: 'circle', image: circle },
  { name: 'heart', image: heart },
  { name: 'square', image: square },
];

function ShapeOptions({ onSelect }) {
  return (
    <div>
      <label className="block font-semibold mb-2">Hình dạng:</label>
      <div className="flex space-x-4">
        {shapes.map(({ name, image }) => (
          <img
            key={name}
            src={image}
            alt={name}
            draggable
            onClick={() => onSelect({ name, image })}
            onDragStart={(e) => {
              e.dataTransfer.setData('type', 'shape');
              e.dataTransfer.setData('data', JSON.stringify({ name, image }));
            }}
            className="w-16 h-16 cursor-move border hover:border-blue-500 rounded shadow"
          />
        ))}
      </div>
    </div>
  );
}

export default ShapeOptions;
