import React, { useState } from "react";

const steps = ["base", "cream", "topping"];

const availableItems = {
  base: ["base_vanilla.png", "base_choco.png"],
  cream: ["cream_white.png", "cream_choco.png"],
  topping: ["topping_strawberry.png", "topping_cherry.png"]
};

export default function CakeBuilder() {
  const [selectedLayers, setSelectedLayers] = useState({});
  const [currentStep, setCurrentStep] = useState("base");
  const [draggingItem, setDraggingItem] = useState(null);

  const handleDragStart = (item) => {
    setDraggingItem(item);
  };

  const handleDrop = () => {
    if (!draggingItem.includes(currentStep)) return;
    setSelectedLayers((prev) => ({
      ...prev,
      [currentStep]: draggingItem
    }));

    const currentIndex = steps.indexOf(currentStep);
    if (currentIndex < steps.length - 1) {
      setCurrentStep(steps[currentIndex + 1]);
    }
    setDraggingItem(null);
  };

  return (
    <div className="flex p-4 gap-4">
      {/* Left Panel - Selection */}
      <div className="w-1/2">
        <h2 className="text-xl font-bold mb-2">Chọn {currentStep}</h2>
        <div className="flex flex-wrap gap-4">
          {availableItems[currentStep].map((item, idx) => (
            <div
              key={idx}
              draggable
              onDragStart={() => handleDragStart(item)}
              className="border p-2 cursor-grab hover:shadow-lg"
            >
              <img
                src={`/images/${item}`}
                alt="item"
                className="w-24 h-24 object-contain"
              />
            </div>
          ))}
        </div>
      </div>

      {/* Right Panel - Cake View */}
      <div
        className="w-1/2 border-2 border-dashed rounded-lg h-96 relative bg-white"
        onDragOver={(e) => e.preventDefault()}
        onDrop={handleDrop}
      >
        <h2 className="text-xl font-bold text-center pt-2">Bánh của bạn</h2>
        <div className="absolute inset-0">
          {steps.map((step) =>
            selectedLayers[step] ? (
              <img
                key={step}
                src={`/images/${selectedLayers[step]}`}
                alt={step}
                className="absolute w-full h-full object-contain"
              />
            ) : null
          )}
        </div>
      </div>
    </div>
  );
}
