// File: src/components/options/SmoothLinesOptions.jsx
import React from 'react';
import circleSmooth from '../../../assets/smooth/circle_sugar.png';
import heartSmooth from '../../../assets/smooth/heart_suger.png';
import squareSmooth from '../../../assets/smooth/square_suger.png';

const smoothMap = {
  circle: circleSmooth,
  heart: heartSmooth,
  square: squareSmooth,
};

function SmoothLinesOptions({ shapeName, onSelect, onSkip }) {
  if (!shapeName) return null;
  const smoothImage = smoothMap[shapeName];

  return (
    <div>
      <label>Đường mịn:</label>
      <div className="flex space-x-4 items-center">
        <img
          src={smoothImage}
          alt="smooth"
          className="w-16 h-16 cursor-pointer border hover:border-blue-500"
          onClick={() => onSelect(smoothImage)}
        />
        <button
          onClick={onSkip}
          className="ml-4 px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
        >
          Bỏ qua
        </button>
      </div>
    </div>
  );
}

export default SmoothLinesOptions;
