import React from 'react';
import Option from './Option';

function CakeControl({
    numLayers,
    shape,
    onSelectNumLayers,
    onSelectShape,
    onSelectBaseColor,
    onSelectMidCream,
    onSelectTopColorSecondLayer,
    onSelectTopCream,
    onSelectSugar,
}) {
    const shapes = [
        { name: 'circle', image: '/images/circle_form.png' },
        { name: 'heart', image: '/images/heart_form.png' },
        { name: 'square', image: '/images/square_form.png' },
    ];

    const baseColors = shape ? [
        { name: 'brown', image: `/images/${shape}_base_brown.png` },
        { name: 'red', image: `/images/${shape}_base_red.png` },
        { name: 'yellow', image: `/images/${shape}_base_yellow.png` },
    ] : [];

    const midCreams = shape ? [
        { name: 'green', image: `/images/${shape}_mid_green.png` },
        { name: 'violet', image: `/images/${shape}_mid_violet.png` },
        { name: 'white', image: `/images/${shape}_mid_white.png` },
    ] : [];

    const topCreams = shape ? [
        { name: 'brown', image: `/images/${shape}_top_brown.png` },
        { name: 'red', image: `/images/${shape}_top_red.png` },
        { name: 'yellow', image: `/images/${shape}_top_yellow.png` },
    ] : [];

    const sugarOption = shape ? [{ name: 'yes', image: `/images/${shape}_sugar.png` }] : [];

    const handleSugarChange = (addSugar) => {
        onSelectSugar(addSugar);
    };

    return (
        <div className="cake-control">
            <h2>Tùy chỉnh bánh</h2>

            {numLayers === null && (
                <div>
                    <h3>Số tầng</h3>
                    <button onClick={() => onSelectNumLayers(1)}>1 tầng</button>
                    <button onClick={() => onSelectNumLayers(2)}>2 tầng</button>
                </div>
            )}

            {numLayers !== null && shape === null && (
                <div>
                    <h3>Hình dạng</h3>
                    <div className="options-grid">
                        {shapes.map((s) => (
                            <Option
                                key={s.name}
                                name={s.name}
                                image={s.image}
                                onClick={() => onSelectShape(s.name)}
                            />
                        ))}
                    </div>
                </div>
            )}

            {shape !== null && (
                <div>
                    <h3>Màu sắc</h3>
                    <div className="options-grid">
                        {baseColors.map((color) => (
                            <Option
                                key={color.name}
                                name={color.name}
                                image={color.image}
                                onClick={() => onSelectBaseColor(color.name)}
                            />
                        ))}
                    </div>
                </div>
            )}

            {numLayers === 2 && shape !== null && baseColors.length > 0 && (
                <div>
                    <h3>Kem ở giữa</h3>
                    <div className="options-grid">
                        {midCreams.map((cream) => (
                            <Option
                                key={cream.name}
                                name={cream.name}
                                image={cream.image}
                                onClick={() => onSelectMidCream(cream.name)}
                            />
                        ))}
                    </div>
                </div>
            )}

            {numLayers === 2 && shape !== null && baseColors.length > 0 && (
                <div>
                    <h3>Màu sắc tầng 2</h3>
                    <div className="options-grid">
                        {baseColors.map((color) => (
                            <Option
                                key={`top2-${color.name}`}
                                name={color.name}
                                image={color.image}
                                onClick={() => onSelectTopColorSecondLayer(color.name)}
                            />
                        ))}
                    </div>
                </div>
            )}

            {shape !== null && baseColors.length > 0 && (
                <div>
                    <h3>Kem phủ</h3>
                    <div className="options-grid">
                        {topCreams.map((cream) => (
                            <Option
                                key={cream.name}
                                name={cream.name}
                                image={cream.image}
                                onClick={() => onSelectTopCream(cream.name)}
                            />
                        ))}
                    </div>
                </div>
            )}

            {shape !== null && (
                <div>
                    <h3>Đường mịn</h3>
                    <div className="options-grid">
                        {sugarOption.map((s) => (
                            <div key={s.name} className="option">
                                <img src={s.image} alt="Đường mịn" style={{ width: '50px', height: '50px', cursor: 'pointer' }} onClick={() => handleSugarChange(true)} />
                            </div>
                        ))}
                        <button onClick={() => handleSugarChange(false)}>Bỏ qua</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CakeControl;
