import React from 'react';

import ShapeOptions from '../builder/steps/ShapeOptions';
import ColorOptions from '../builder/steps/ColorOptions';
import CreamMiddleOptions from '../builder/steps/CreamMiddleOptions';
import ColorTier2Options from '../builder/steps/ColorTier2Options';
import CreamTopOptions from '../builder/steps/CreamTopOptions';
import SmoothLinesOptions from '../builder/steps/SmoothLinesOptions';

// ✅ Thêm component chọn số tầng
function TierSelector({ tierCount, onSelect }) {
  return (
    <div>
      <label>Số tầng:</label>
      <div className="flex space-x-4 mt-2">
        {[1, 2].map((count) => (
          <button
            key={count}
            className={`px-4 py-2 border rounded ${
              tierCount === count ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
            onClick={() => onSelect(count)}
          >
            {count} tầng
          </button>
        ))}
      </div>
    </div>
  );
}

function Sidebar({
  onSelectShape,
  onSelectColorTier1,
  onSelectCreamMiddle,
  onSelectColorTier2,
  onSelectCreamTop,
  onSelectSmoothLine,
  onSelectTierCount,
  selectedShape,
  tierCount
}) {
  return (
    <div className="w-1/2 p-4 space-y-6 overflow-auto max-h-screen">
      <TierSelector tierCount={tierCount} onSelect={onSelectTierCount} />
      <ShapeOptions onSelect={onSelectShape} />
      <ColorOptions shapeName={selectedShape?.name} onSelect={onSelectColorTier1} />
      
      {/* Chỉ hiển thị nếu là bánh 2 tầng */}
      {tierCount === 2 && (
        <>
          <CreamMiddleOptions shapeName={selectedShape?.name} onSelect={onSelectCreamMiddle} />
          <ColorTier2Options shapeName={selectedShape?.name} onSelect={onSelectColorTier2} />
        </>
      )}

      <CreamTopOptions shapeName={selectedShape?.name} onSelect={onSelectCreamTop} />
      <SmoothLinesOptions
        shapeName={selectedShape?.name}
        onSelect={onSelectSmoothLine}
        onSkip={() => onSelectSmoothLine(null)}
      />
    </div>
  );
}

export default Sidebar;
