import React from 'react';
import circle1 from '../../../assets/colors/circle_base_brown.png';
import circle2 from '../../../assets/colors/circle_base_red.png';
import circle3 from '../../../assets/colors/circle_base_yellow.png';
import heart1 from '../../../assets/colors/heart_base_brown.png';
import heart2 from '../../../assets/colors/heart_base_red.png';
import heart3 from '../../../assets/colors/heart_base_yellow.png';
import square1 from '../../../assets/colors/square_base_brown.png';
import square2 from '../../../assets/colors/square_base_red.png';
import square3 from '../../../assets/colors/square_base_yellow.png';

const colorMap = {
  circle: [circle1, circle2, circle3],
  heart: [heart1, heart2, heart3],
  square: [square1, square2, square3],
};

function ColorOptions({ shapeName, onSelect }) {
  if (!shapeName) return null;
  const colors = colorMap[shapeName];

  return (
    <div>
      <label className="block font-semibold mb-2">Màu sắc tầng 1:</label>
      <div className="flex space-x-4">
        {colors.map((src, index) => (
          <img
            key={index}
            src={src}
            alt={`color-${index}`}
            draggable
            onClick={() => onSelect(src)}
            onDragStart={(e) => {
              e.dataTransfer.setData('type', 'colorTier1');
              e.dataTransfer.setData('text/data', src);
            }}
            className="w-16 h-16 cursor-move border hover:border-blue-500 rounded shadow"
          />
        ))}
      </div>
    </div>
  );
}

export default ColorOptions;
