import React from 'react';
import './CakeCanvas.css';
import Layer from './Layer';

const CANVAS_WIDTH = 300;
const CANVAS_HEIGHT = 300;
// Vị trí gốc cho lớp bánh đầu tiên (hình dạng/màu sắc cơ bản)
// Đặt BASE_X và BASE_Y để hình ảnh 100x100px được căn giữa trong canvas 300x300px
// (300 / 2) - (100 / 2) = 150 - 50 = 100
// Hoặc đơn giản hơn, BASE_X và BASE_Y là tâm của canvas, và các lớp sẽ được dịch chuyển tương ứng
const BASE_X = CANVAS_WIDTH / 2;
const BASE_Y = CANVAS_HEIGHT / 2 + 50; // Điều chỉnh vị trí trung tâm theo chiều dọc để bánh không quá cao

function CakeCanvas({ layers, onDrop, numLayers }) {
    return (
        <div
            className="cake-canvas"
            style={{ width: `${CANVAS_WIDTH}px`, height: `${CANVAS_HEIGHT}px` }}
            onDrop={onDrop}
            onDragOver={(e) => e.preventDefault()}
        >
            {layers.map((layer, index) => {
                // Khởi tạo vị trí và kích thước cho mỗi lớp
                // x, y là góc trên bên trái của hình ảnh 100x100px, được căn giữa tại BASE_X, BASE_Y
                let x = BASE_X - 50; // 50 là một nửa chiều rộng/cao mặc định (100px)
                let y = BASE_Y - 50;
                let width = 100;
                let height = 100;
                let zIndex = index + 1; // Đảm bảo các lớp mới hơn nằm trên các lớp cũ hơn

                switch (layer.type) {
                    case 'shape':
                        // Hình dạng cơ bản, không có offset đặc biệt
                        break;
                    case 'base':
                        // Màu sắc cơ bản, nằm trên hình dạng, không có offset đặc biệt
                        break;
                    case 'midCream':
                        // Kem ở giữa, dịch lên trên 22.6px so với lớp dưới
                        y -= 22.6;
                        break;
                    case 'topColorSecondLayer':
                        // Màu sắc tầng 2: giảm kích thước và dịch chuyển theo yêu cầu
                        width *= 0.8;
                        height *= 0.8;
                        x += 37.6; // Loại bỏ phép chia cho 2
                        y -= 61.9; // Loại bỏ phép chia cho 2
                        break;
                    case 'topCream':
                        // Kem phủ: vị trí khác nhau tùy thuộc vào số tầng
                        if (numLayers === 1) {
                            // Bánh 1 tầng: dịch lên trên 22.6px
                            y -= 22.6;
                        } else if (numLayers === 2) {
                            // Bánh 2 tầng: giảm kích thước và dịch chuyển theo yêu cầu
                            width *= 0.8;
                            height *= 0.8;
                            x += 37.6; // Loại bỏ phép chia cho 2
                            y -= 61.9; // Loại bỏ phép chia cho 2
                        }
                        break;
                    case 'sugar':
                        // Đường mịn: vị trí khác nhau tùy thuộc vào số tầng
                        if (numLayers === 1) {
                            // Bánh 1 tầng: dịch lên trên 22.6px
                            y -= 22.6;
                        } else if (numLayers === 2) {
                            // Bánh 2 tầng: giảm kích thước và dịch chuyển theo yêu cầu
                            width *= 0.8;
                            height *= 0.8;
                            x += 37.6; // Loại bỏ phép chia cho 2
                            y -= 61.9; // Loại bỏ phép chia cho 2
                        }
                        break;
                    default:
                        break;
                }

                return (
                    <Layer
                        key={index}
                        src={layer.src}
                        alt={layer.type}
                        style={{
                            position: 'absolute',
                            left: `${x}px`,
                            top: `${y}px`,
                            width: `${width}px`,
                            height: `${height}px`,
                            zIndex: zIndex,
                        }}
                    />
                );
            })}
        </div>
    );
}

export default CakeCanvas;
