import React, { useState, useRef } from 'react';
import CakePreview from './components/preview/CakePreview';
import Sidebar from './components/preview/Sidebar';

function App() {
  const [selectedShape, setSelectedShape] = useState(null);
  const [selectedColorTier1, setSelectedColorTier1] = useState(null);
  const [selectedCreamMiddle, setSelectedCreamMiddle] = useState(null);
  const [selectedColorTier2, setSelectedColorTier2] = useState(null);
  const [selectedCreamTop, setSelectedCreamTop] = useState(null);
  const [selectedSmoothLine, setSelectedSmoothLine] = useState(null);

  const cakeLayers = [
    selectedShape?.image,
    selectedColorTier1,
    selectedCreamMiddle,
    selectedColorTier2,
    selectedCreamTop,
    selectedSmoothLine,
  ].filter(Boolean);

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Bên trái: Sidebar chọn */}
      <Sidebar
        onSelectShape={setSelectedShape}
        onSelectColorTier1={setSelectedColorTier1}
        onSelectCreamMiddle={setSelectedCreamMiddle}
        onSelectColorTier2={setSelectedColorTier2}
        onSelectCreamTop={setSelectedCreamTop}
        onSelectSmoothLine={setSelectedSmoothLine}
        selectedShape={selectedShape}
      />

      {/* Bên phải: Hiển thị bánh */}
      <div className="w-1/2 p-4 flex items-center justify-center">
        <CakePreview layers={cakeLayers} />
      </div>
    </div>
  );
}

export default App;
