import React from 'react';
import circle1 from '../../assets/top/circle_top_brown.png';
import circle2 from '../../assets/top/circle_top_red.png';
import circle3 from '../../assets/top/circle_top_yellow.png';
import heart1 from '../../assets/top/heart_top_brown_.png';
import heart2 from '../../assets/top/heart_top_red.png';
import heart3 from '../../assets/top/heart_top_yellow.png';
import square1 from '../../assets/top/square_top_brown.png';
import square2 from '../../assets/top/square_top_red.png';
import square3 from '../../assets/top/square_top_yellow.png';

const topMap = {
  circle: [circle1, circle2, circle3],
  heart: [heart1, heart2, heart3],
  square: [square1, square2, square3],
};

function CreamTopOptions({ shapeName, onSelect }) {
  if (!shapeName) return null;
  const creams = topMap[shapeName];

  return (
    <div>
      <label>Kem phủ:</label>
      <div className="flex space-x-4">
        {creams.map((src, index) => (
          <img
            key={index}
            src={src}
            alt={`top-${index}`}
            className="w-16 h-16 cursor-pointer border hover:border-blue-500"
            onClick={() => onSelect(src)}
          />
        ))}
      </div>
    </div>
  );
}

export default CreamTopOptions;
