import React from 'react';
import './Option.css';

function Option({ name, image, onClick, itemType }) { // Thêm itemType vào props
    const handleDragStart = (e) => {
        // Đóng gói thông tin về item đang kéo vào dataTransfer
        e.dataTransfer.setData('application/json', JSON.stringify({ itemType: itemType, itemName: name }));
    };

    return (
        <div
            className="option"
            onClick={onClick}
            draggable="true" // Cho phép kéo
            onDragStart={handleDragStart} // Xử lý sự kiện bắt đầu kéo
        >
            <img src={image} alt={name} />
            {/* <p>{name}</p> */}
        </div>
    );
}

export default Option;
